import React, { useState } from "react";
import "./Fos.css";

const faszsagok = [
  "pina",
  "fasz",
  "fos",
  "geci",
  "pisa",
  "segglyuk",
  "seggpina",
  "hugyospite",
  "gecilyuk",
  "zsido",
];

const HandleClick = () => {
  const [fosState, setFosState] = useState([]);

  const randomnumber = Math.floor(Math.random() * 10);
  const newFaszsag = faszsagok[randomnumber];

  const addFaszsag = () => {
    setFosState((fosState) => [newFaszsag]);
  };
};
const Fos = (props) => {
  return (
    <button onClick={HandleClick} className="fos">
      {props.children}
    </button>
  );
};

export default Fos;
