import React, { useState } from "react";

const Star = () => {
  const [buttonText, setButtonText] = useState("Tetszik?");

  const HandleClick = () => {
    if (buttonText === "Tetszik?") {
      setButtonText((buttonText) => ":)");
    } else {
      setButtonText((buttonText) => "Tetszik?");
    }
  };

  return <button onClick={HandleClick}>{buttonText}</button>;
};

export default Star;
