import React, { useState } from "react";
import "./Fos.css";

const faszsagok = [
  "pina",
  "fasz",
  "fos",
  "geci",
  "pisa",
  "segglyuk",
  "seggpina",
  "hugyospite",
  "gecilyuk",
  "zsido",
  "budosszar",
  "kurva",
  "kurva anyad",
  "retkes pinabofoges",
  "buzi",
  "veres pocsszar",
];

const Fos = (props) => {
  const [fosState, setFosState] = useState([]);

  const HandleClick = () => {
    const randomnumber = Math.floor(Math.random() * 10);
    const newFaszsag = faszsagok[randomnumber];

    setFosState((fosState) => ["-" + newFaszsag + "-"]);
  };

  return (
    <div>
      <button onClick={HandleClick} className="fos">
        {props.children}
      </button>
      <div className="faszsagok">{fosState}</div>
    </div>
  );
};

export default Fos;
