import React, { useState } from "react";

const Star = () => {
  const [buttonText, setButtonText] = useState("Good?");

  const HandleClick = () => {
    console.log(buttonText);
    if (buttonText === "Good?") {
      setButtonText((buttonText) => "Good! :)");
    } else {
      setButtonText((buttonText) => "Good?");
    }
  };

  return <button onClick={HandleClick}>{buttonText}</button>;
};

export default Star;
