import React, { useState } from "react";
import "./Fos.css";
import Star from "./Stars.js";

const faszsagok = [
  "te hulye fasz",
  "baszodj meg",
  "vorhenyes seggnyalo faszkalap",
  "szarjal sunt",
  "te fasszopo",
  "buzi vagy",
  "megbaszom anyad",
  "te moslek",
  "budos zsido",
  "zsido",
  "szaros seggu",
  "kurva",
  "kurva anyad",
  "retkes pinabofoges",
  "buzi",
  "veres pocsszar",
  "fallosz",
  "loszar",
  "pisa a seggembol",
  "egyel gecit",
  "medvezsido",
];

const Fos = (props) => {
  const [fosState, setFosState] = useState([]);

  const AddToFavorites = () => {
    console.log("works");
    alert(fosState + " was added to favorites");
  };

  const HandleClick = () => {
    const randomnumber = Math.floor(Math.random() * faszsagok.length);
    const newFaszsag = faszsagok[randomnumber];

    setFosState((fosState) => ["-" + newFaszsag + "-"]);
  };
  const showStar = fosState.length > 0;
  return (
    <div>
      <span onClick={HandleClick} className="fos">
        <a></a>
      </span>
      <div className="faszsagok">
        {fosState}
        {showStar && <Star />}
      </div>
    </div>
  );
};

export default Fos;
