import React, { useState } from "react";
import "./Fos.css";

const faszsagok = [
  "pina",
  "fasz",
  "fos",
  "geci",
  "pisa",
  "segglyuk",
  "seggpina",
  "hugyospite",
  "gecilyuk",
  "zsido",
];

const Fos = (props) => {
  const [fosState, setFosState] = useState([]);

  const HandleClick = () => {
    const randomnumber = Math.floor(Math.random() * 10);
    const newFaszsag = faszsagok[randomnumber];

    setFosState((fosState) => [...fosState, newFaszsag + " "]);
  };

  return (
    <div>
      <button onClick={HandleClick} className="fos">
        {props.children}
      </button>
      <ul>
        {faszsagok.map((item) => (
          <li key={item.id}>{item.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default Fos;
